mapper.select:
SELECT c.CBWA09,d.CBLA09,g.CBPA07,f.CBPA07,h.CALA08,e.CBPB12,e.CBPB15,a.CBIG10,a.CBIG15 FROM CBIG a LEFT JOIN CBIE b ON a.CBIE01=b.CBIE01 LEFT JOIN CBWA c ON b.CBIE09=c.CBWA01 LEFT JOIN CBLA d ON d.CBLA01=a.CBIG08 LEFT JOIN CBPB e ON e.CBPB01=a.CBIG09 LEFT JOIN CBPA f ON f.CBPA01=e.CBPB14 LEFT JOIN CBPA g ON f.CBPA09=g.CBPA01 LEFT JOIN CALA h ON h.CALA01=e.CBPB10
WHERE a.CBIG07=0
and c.CBWA09 =#{cbwa09}
and d.CBLA09 like concat('%', #{cbla09}, '%')
and a.cbig09 =#{cbig09}
and a.cbig10 like concat('%', #{cbig10}, '%')
and e.cbpb01 =#{cbpb01}

SELECT b.CBWA09,a.CBIB04,a.CBIB03,a.CBIB17,a.CBIB06,c.CBPB08, a.CBIB09,a.CBIB10,b.CBWA11,a.CBIB12,a.CBIB13,a.CBIB14,a.CBIB15,a.CBIB16 FROM CBIB a LEFT JOIN CBWA b on a.CBIB02=b.CBWA01 LEFT JOIN CBPB c ON c.CBPB01=a.CBIB08 WHERE b.CBWA06=0
AND a.CBIB17 =#{cbib17}
and b.CBWA09 =#{cbwa09}
and date_format(a.cbib04,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
and date_format(a.cbib04,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
# LIMIT 100
