mapper.select:
SELECT ob.CBOB01 AS id, oa.CBOA07 AS orderNo, ob.CBOB18 AS totalOrderNo, ob.CBOB09 AS orderQty, ba.CBBA15 AS priority, ba.CBBA13 AS goodsNum, pb.CBPB10 AS brand, pb.CBPB01 AS goodsId, pb.CBPB12 AS model, pb.CBPB08 AS description, ba.CBBA13 AS makeQty, ob.confirm_qty AS confirmQty
FROM `CBOB` AS ob LEFT JOIN CBBA ba ON ba.CBBA01=ob.CBOB17 LEFT JOIN CBOA oa on oa.CBOA01 = ob.CBOA01 LEFT JOIN CBPB pb ON ob.CBOB08 = pb.CBPB01
where oa.CBOA06=0 AND oa.CBOA27=1 AND oa.CBOA11!=0 AND oa.CBOA11!=6 AND oa.confirm_sku_status =2
and oa.CBOA07=#{orderNo}
and ob.CBOB18=#{totalOrderNo}
and pb.CBPB01=#{goodsId}
order by ob.CBOB03 DESC

SELECT oa.CBOA01 as id, oa.CBOA07 as orderNo, oa.CBOA25 as customerNo, oa.CBOA02 as createTime, ca.CBCA08 as customerName, u.nick_name as saleUser, oa.CBOA16 AS settleCurrent, oa.CBOA17 as receiver, oa.CBOA18 as address, oa.CBOA19 as phone, oa.CBOA27 as orderClass, oa.CBOA24 as orderType, oa.CBOA08 as orderDate, oa.CBOA11 as status, oa.confirm_sku_status as confirmSkuStatus
FROM `CBOA` oa LEFT JOIN CBCA ca on oa.CBOA09 =ca.CBCA01 LEFT JOIN sys_user u ON u.user_id =oa.CBOA10 where oa.CBOA06=0
and oa.CBOA08 >= #{startTime}
and oa.CBOA08 <=#{endTime}
and oa.CBOA11 =#{status}
and (oa.CBOA11 =5 or oa.CBOA11 =2)
and oa.CBOA07 like concat('%', #{orderNo}, '%')
and oa.CBOA27 =#{type}
and ca.CBCA08 like concat('%', #{customer}, '%')
order by oa.CBOA02 DESC

SELECT oa.CBOA01 as id, oa.CBOA07 as orderNo, oa.CBOA25 as customerNo, oa.CBOA02 as createTime, ca.CBCA08 as customerName, u.nick_name as saleUser, oa.CBOA16 AS settleCurrent, oa.CBOA17 as receiver, oa.CBOA18 as address, oa.CBOA19 as phone, oa.CBOA27 as orderClass, oa.CBOA24 as orderType, oa.CBOA08 as orderDate, oa.CBOA11 as status, oa.confirm_sku_status as confirmSkuStatus
FROM `CBOA` oa LEFT JOIN CBCA ca on oa.CBOA09 =ca.CBCA01 LEFT JOIN sys_user u ON u.user_id =oa.CBOA10 where oa.CBOA06=0
and oa.CBOA08 >= #{startTime}
and oa.CBOA08 <=#{endTime}
and oa.CBOA11 =#{status}
and (oa.CBOA11 =5 or oa.CBOA11 =2)
and oa.CBOA07 like concat('%', #{orderNo}, '%')
and oa.CBOA27 =#{type}
and ca.CBCA08 like concat('%', #{customer}, '%')
order by oa.CBOA02 DESC
