mapper.select:
SELECT ca.CBCA08 AS customerName, oa.CBOA02 AS createTime, USER.nick_name AS saleUser, oa.CBOA07 AS saleOrderNo, pb.CBPB12 AS model, pb.CBPB10 AS brand, ob.CBOB09 AS qty, ob.CBOB12 AS price FROM CBOB ob LEFT JOIN CBOA oa ON oa.CBOA01 = ob.CBOA01 LEFT JOIN CBCA ca ON ca.CBCA01 = oa.CBOA09 LEFT JOIN sys_user USER ON USER.user_id = oa.CBOA10 LEFT JOIN CBPB pb ON pb.CBPB01 = ob.CBOB08 WHERE oa.CBOA11 = 5 AND ob.CBOB07 = 0
AND oa.CBOA09 = #{customerId}
AND pb.CBPB12 = #{model}
AND pb.CBPB10 = #{brand}
AND oa.CBOA10 = #{saleUserId}
AND oa.CBOA02 >= #{startTime}
AND oa.CBOA02 <=#{endTime}

SELECT ca.CBCA08 AS customerName, oa.CBOA02 AS createTime, ua.CAUA09 AS saleUser, oa.CBOA07 AS saleOrderNo, pb.CBPB12 AS model, pb.CBPB10 AS brand, ob.CBOB09 AS qty, ob.CBOB12 AS price, sc.CBSB01 FROM CBOB ob LEFT JOIN CBOA oa ON oa.CBOA01 = ob.CBOA01 LEFT JOIN CBCA ca ON ca.CBCA01 = oa.CBOA09 LEFT JOIN CAUA ua ON ua.CAUA01 = oa.CBOA10 LEFT JOIN CBPB pb ON pb.CBPB01 = ob.CBOB08 LEFT JOIN CBSC sc ON sc.CBSC14 = ob.CBOB01 WHERE oa.CBOA11 = 5 AND ob.CBOB07 = 0
AND oa.CBOA09 = #{customerId}
AND sa.CBSA01 = #{supplierId}
AND pb.CBPB12 = #{model}
AND pb.CBPB10 = #{brand}
AND oa.CBOA10 = #{saleUserId}
AND oa.CBOA02 >= #{startTime}
AND oa.CBOA02 <=#{endTime}
