mapper.select:
SELECT oa.CBOC01 as id, oa.CBOC07 as orderNo, oa.CBOC25 as customerNo, oa.CBOC02 as createTime, ca.CBCA08 as customerName, u.nick_name as saleUser, oa.CBOC16 AS settleCurrent, oa.CBOC17 as receiver, oa.CBOC18 as address, oa.CBOC19 as phone, oa.CBOC24 as orderType, oa.CBOC08 as orderDate, oa.CBOC11 as status, oa.CBOA20 as fcNumber, oa.CBOA21 as other
FROM `CBOC` oa LEFT JOIN CBCA ca on oa.CBOC09 =ca.CBCA01 LEFT JOIN sys_user u ON u.user_id =oa.CBOC10 LEFT JOIN CBOA AS cboa on cboa.CBOA01 = oa.CBOC26 where oa.CBOC06=0
and oa.CBOC08 >= #{startTime}
and oa.CBOC08 <=#{endTime}
and oa.CBOC11 =#{status]}
and oa.CBOC07 like concat('%', #{orderNo}, '%')
and oa.CBOC24 =#{type}
and ca.CBCA08 like concat('%', #{customer}, '%')
and cboa.CBOA07 like concat('%', #{saleNo}, '%')
order by oa.CBOC02 DESC
