mapper.select:
SELECT ob.CBOB08 AS goodsId, ob.CBOB01 AS cbobId, la.CALA08 AS brand, pb.CBPB12 AS model, pb.CBPB08 AS description FROM CBOB ob
LEFT JOIN CBPB pb ON CBPB01 = ob.CBOB08 LEFT JOIN CALA la ON la.CALA01 =pb.CBPB10 AND la.CALA10 ='商品品牌' WHERE ob.CBOA01 = #{orderId}
AND ( la.CALA08 like concat('%', #{selectMsg}, '%') OR pb.CBPB12 like concat('%', #{selectMsg}, '%') OR pb.CBPB08 like concat('%', #{selectMsg}, '%') )
