mapper.select:
SELECT pk.CBPK01 as id, pk.CBPK07 as orderNo, pk.CBPK30 as customerNo, ca.CBCA08 as customerName, ca.CBCA28 as customerLevel, pk.CBPK18 as contacts, pk.CBPK19 as phone, us.nick_name as saleUser, wa.CBWA09 as whName, pk.CBPK21 as address, pk.CBPK08 as orderDate, pk.CBPK11 as status, pk.check_status as checkStatus
FROM `CBPK` pk LEFT JOIN CBCA as ca on ca.CBCA01=pk.CBPK09 LEFT JOIN sys_user us on us.user_id=pk.CBPK17 LEFT JOIN CBWA wa on wa.CBWA01=pk.CBPK10 where pk.CBPK06=0
and pk.CBPK08 >= #{startTime}
and pk.CBPK08 <=#{endTime}
and ca.CBCA08 like concat('%', #{customerName}, '%')
and pk.CBPK11 =#{status}
and pk.check_status =#{checkStatus}
and wa.CBWA01 = #{whName}
and pk.CBPK07 like concat('%', #{orderNo}, '%')
order by pk.CBPK02 desc

SELECT pk.CBPK01 as id, pk.CBPK07 as orderNo, pk.CBPK30 as customerNo, ca.CBCA08 as customerName, ca.CBCA28 as customerLevel, pk.CBPK18 as contacts, pk.CBPK19 as phone, us.nick_name as saleUser, wa.CBWA09 as whName, pk.CBPK21 as address, pk.CBPK08 as orderDate, pk.CBPK11 as status, pk.check_status as checkStatus
FROM `CBPK` pk LEFT JOIN CBCA as ca on ca.CBCA01=pk.CBPK09 LEFT JOIN sys_user us on us.user_id=pk.CBPK17 LEFT JOIN CBWA wa on wa.CBWA01=pk.CBPK10 LEFT JOIN CBSC cbsc on cbsc.takegoodsid=pk.CBPK01 LEFT JOIN CBSB cbsb on cbsb.CBSB01 = cbsc.CBSB01 AND cbsb.CBSB06=0 AND cbsb.CBSB11!=2 AND cbsb.CBSB11!=4 where pk.CBPK06=0
and pk.CBPK08 >= #{startTime}
and pk.CBPK08 <=#{endTime}
and ca.CBCA08 =#{customerName}
and pk.CBPK10 =#{whId}
and pk.CBPK11 =#{status}
and pk.check_status =#{checkStatus}
and wa.CBWA01 = #{whName}
and pk.CBPK07 like concat('%', #{orderNo}, '%')
GROUP BY pk.CBPK07 order by pk.CBPK02 desc

SELECT a.CBSB07 as cbsb07, c.CBCA09 as cbca09, a.CBSB08 as cbsb08, c.CBCA08 as cbca08, d.CBWA09 as cbwa09, i.nick_name as caua15, j.CBSD03 as cbsd03, a.CBSB17 as cbsb17, a.CBSB10 as cbsb10, a.CBSB09 as cbsb09, c.CBCA28 as cbca28, e.CALA08 as cny, a.CBSB18 as cbsb18, a.CBSB31 as cbsb31, b.CBSC01 as cbsc01, b.takegoodsid as cbsb20, a.CBSB19 as cbsb19, a.CBSB21 as cbsb21, f.CBSA08 as cbsa08, b.CBSC17 as cbsc17, h.CALA08 as cala08, g.CBPB12 as cbpb12, g.CBPB08 as cbpb08, g.CBPB15 as cbpb15, b.CBSC08 as cbsc08, b.CBSC09 as cbsc09, b.CBSC11 as cbsc11, b.CBSC12 as cbsc12, b.CBSC13 as cbsc13, j.CBSD09 as cbsd09, k.CBLA09 as cbla09, j.CBSD11 as cbsd11, a.CBSB01 as cbsb01, a.CBSB30 as cbsb30, b.CBSC02 as cbsc02, l.CBPA07 as cbpa07, j.CBSD02 as cbsd02
FROM CBSB a LEFT JOIN CBSC b on a.CBSB01=b.CBSB01 LEFT JOIN CBCA c ON c.CBCA01=a.CBSB09 LEFT JOIN CBWA d ON d.CBWA01=a.CBSB10 LEFT JOIN CALA e on e.CALA01=a.CBSB16 LEFT JOIN CBSA f ON f.CBSA01=b.CBSC15 LEFT JOIN CBPB g on g.CBPB01=b.CBSC08 LEFT JOIN CALA h ON h.CALA01=g.CBPB10 LEFT JOIN sys_user i on i.user_id=a.CBSB17 LEFT JOIN CBSD j on j.CBSB01=a.CBSB01 LEFT JOIN CBLA k ON k.CBLA01=j.CBSD10 LEFT JOIN CBPA l ON g.CBPB14=l.CBPA01 WHERE a.CBSB01 = #{cbsb01} and d.cbwa09 = #{cbwa09}
and date_format(a.cbsb08,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
and date_format(a.cbsb08,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
/* GROUP BY b.CBSC08*/
