mapper.select:
SELECT sc.CBSC09 as qty,sc.CBSC11 as price, pb.CBPB10 AS brand,pb.CBPB12 AS model,pb.CBPB08,ca.CBCA08 AS customerName, sc.CBSC12 as money FROM CBSC sc LEFT JOIN CBSB sb ON sb.CBSB01 = sc.CBSB01 LEFT JOIN CBPB pb ON pb.CBPB01 = sc.CBSC08 LEFT JOIN CBCA ca ON ca.CBCA01 = sb.CBSB09 WHERE 1=1
AND sb.CBSB09 = #{customerId}
AND pb.CBPB12 like concat('%', #{model}, '%')
AND pb.CBPB10 = #{brand}
AND sc.CBSC03 >= #{startTime}
AND sc.CBSC03 <=#{endTime}
order by sc.CBSC03 desc

SELECT sc.CBSC03 AS outWhTime, sc.CBSC09 AS qty, ob.CBOB11 AS rPrice, oa.CBOA07 AS orderNo, oa.CBOA16 AS currency, ca.CBCA09 AS suplierName, pb.CBPB08 AS description, pb.CBPB10 AS brand, pb.CBPB12 AS model,
-- pe.CBPE03 AS inWhTime, sa.CBSA08 AS gc -- pd.CBPD11 AS cgRprice, -- pc.CBPC16,ob.CBOB18 as totalOrderNo FROM CBSC sc LEFT JOIN CBSB sb on sb.CBSB01 =sc.CBSB01 LEFT JOIN CBOB ob ON ob.CBOB01 = sc.CBSC14 LEFT JOIN CBOA oa ON oa.CBOA01 = ob.CBOA01 LEFT JOIN CBCA ca ON ca.CBCA01 = oa.CBOA09 LEFT JOIN CBPB pb on sc.CBSC08 =pb.CBPB01 LEFT JOIN CBSA sa ON sa.CBSA01 = sc.CBSC15 where pb.type = 0
AND oa.CBOA07 like concat('%', #{saleOrderNo}, '%')
AND sb.CBSB10 = #{whId}
AND oa.CBOA09 = #{customerId}
and sc.CBSC03 >= #{startTime}
and sc.CBSC03 <=#{endTime}
ORDER BY sc.CBSC03 DESC -- LEFT JOIN CBPE pe ON pe.CBPE09 = sd.CBSD09 -- LEFT JOIN CBPD pd on pd.CBPC01 =pe.CBPC01 and pd.CBPD08 =pe.CBPE08 -- LEFT JOIN CBPC pc ON pc.CBPC01 = pd.CBPC01 --
