mapper.select:

where
    CBWA15 =1
    and
    CBWA06=0
       and CBWA01 = #{cbwa01}
       and CBWA02 = #{cbwa02}
       and CBWA03 = #{cbwa03}
       and CBWA04 = #{cbwa04}
       and CBWA05 = #{cbwa05}
       and CBWA07 = #{cbwa07}
       and CBWA08 = #{cbwa08}
       and CBWA09 like concat('%',  #{cbwa09}, '%')
       and CBWA10 = #{cbwa10}
       and CBWA11 = #{cbwa11}
       and CBWA12 = #{cbwa12}
       and CBWA13 = #{cbwa13}
       and CBWA14 = #{cbwa14}
       and CBWA15 = #{cbwa15}
    order by CBWA02 desc
