mapper.select:
SELECT

    FROM
	`gs_goods_sn` sn
WHERE
	sn.goods_id = #{goodsId}
	AND sn.wh_id = #{whId}
	AND sn.status=1
ORDER BY
	sn.create_time
	LIMIT #{lockQty}

SELECT s.id,s.goods_id,s.location_id,s.wh_id, s.sn,la.CBLA09,pb.CBPB10,pb.CBPB12,pb.CBPB08 FROM `gs_goods_sn` s left join CBPB pb on pb.CBPB01 = s.goods_id left join CBLA la on la.CBLA01 = s.location_id WHERE (s.delete_flag =0 or s.delete_flag is null)
and s.sn = #{sn}
ORDER BY s.create_time desc
