mapper.select:
SELECT so.id AS id, so.order_no AS orderNo, so.order_date AS orderDate, a.CBSA08 AS supplier, b.CAUA17 AS saler, c.CBCA08 AS customer, d.cbwa09 AS wh, so.status AS status, so.* FROM `gs_ Sales_orders` AS so
LEFT JOIN CBSA a on a.CBSA01=so.supplier_id LEFT JOIN CAUA b on b.CAUA01=so.saler_id LEFT JOIN CBCA c ON c.CBCA01=so.customer_id LEFT JOIN CBWA d ON d.CBWA01=so.wh_id WHERE so.delete_flag=0
and so.order_no like concat('%', #{orderNo}, '%')
and so.order_date >= #{startTime}
and so.order_date <=#{endTime}

SELECT so.id AS id, so.order_no AS orderNo, so.order_date AS orderDate, a.CBSA08 AS supplier, b.CAUA17 AS saler, c.CBCA08 AS customer, d.cbwa09 AS wh, ss.goods_id AS goodsId, ss.price AS price, ss.qty AS qty, ss.remark AS remark FROM `gs_ Sales_orders` AS so
LEFT JOIN gs_Sales_orders_details ss ON so.id=ss.gs_Sales_orders
LEFT JOIN CBSA a on a.CBSA01=so.supplier_id LEFT JOIN CAUA b on b.CAUA01=so.saler_id LEFT JOIN CBCA c ON c.CBCA01=so.customer_id LEFT JOIN CBWA d ON d.CBWA01=so.wh_id where so.id= #{id}

SELECT distinct so.id AS id, so.order_no AS orderNo, so.order_date AS orderDate, a.CBSA08 as cbsa08, b.CAUA17 as caua17, c.CBCA08 as cbca08, d.cbwa09 as cbwa09, ss.goods_id AS goodsId, ss.price AS price, ss.qty AS qty, ss.remark AS remark, e.in_qty AS in_qty, f.qty AS qtys, g.CBPB08 AS cbpb08, h.CALA08 AS cala08, g.CBPB12 AS cbpb12 FROM `gs_ Sales_orders` AS so
LEFT JOIN gs_Sales_orders_details ss ON so.id=ss.gs_Sales_orders
LEFT JOIN CBSA a on a.CBSA01=so.supplier_id LEFT JOIN CAUA b on b.CAUA01=so.saler_id LEFT JOIN CBCA c ON c.CBCA01=so.customer_id LEFT JOIN CBWA d ON d.CBWA01=so.wh_id LEFT JOIN `gs_ Sales_orders_in` e ON e.gs_sales_orders=so.id LEFT JOIN `gs_ Sales_orders_change` f ON f.gs_Sales_orders=so.id LEFT JOIN CBPB g ON g.CBPB01=ss.goods_id LEFT JOIN CALA h ON g.CBPB10=h.CALA01 where so.delete_flag=0
and so.order_date >= #{startTime}
and so.order_date <=#{endTime}
