mapper.select:
SELECT b.* FROM CBOA a LEFT JOIN CABRAA b ON a.CBOA07=b.CABRAA14 LEFT JOIN gs_work_instance c ON c.order_no=b.CABRAA14 WHERE b.CABRAA06=0 and b.CABRAA14 = #{cabraa14}
and b.CABRAA15 >= #{startTime}
and b.CABRAA15 <=#{endTime}
and date_format(a.CBOA08,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
and date_format(a.CBOA08,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')
order by b.CABRAA15 desc

SELECT b.CABRAA02 as cabraa02, b.CABRAA14 as cabraa14, b.CABRAA07 as cabraa07, b.CABRAA01 as cabraa01, b.CABRAA15 as cabraa15, b.CABRAA17 as cabraa17, b.CABRAA21 as cabraa21 FROM CABRAA b LEFT JOIN CBOC c
ON c.CBOC07=b.CABRAA14 LEFT JOIN CBOA a ON a.CBOA07=b.CABRAA14 WHERE b.CABRAA06=0 and a.CBOA11 not in(4,6) and b.CABRAA14 = #{cabraa14}
and b.CABRAA15 >= #{startTime}
and b.CABRAA15 <=#{endTime}
order by b.CABRAA15 desc # or # c.CBOC11!=6

SELECT b.* FROM CABRAA b
LEFT JOIN CBOC c ON c.CBOC07=b.CABRAA14 LEFT JOIN CBOA a ON a.CBOA07=b.CABRAA14 WHERE a.cboa11 in (2,5)
and b.CABRAA06=0 and b.CABRAA14 = #{cabraa14}
and b.CABRAA15 >= #{startTime}
and b.CABRAA15 <=#{endTime}
order by b.CABRAA15 desc
